mapper.update:

	update MEMBER 
	SET MEM_DEL_GB = 'Y'
	WHERE MEM_ID = #{MEM_ID}

mapper.select:

	SELECT MEM_NAME, MEM_RANK, MEM_POINT
	FROM MEMBER
	WHERE MEM_ID = #{MEM_ID}

mapper.delete:
		
			DELETE FROM JJIM
			WHERE JJIM_NUM = #{JJIM_NUM}
			AND MEM_NUM = #{MEM_NUM}

mapper.update:

	UPDATE QNA SET 
		QNA_TITLE = #{QNA_TITLE},
		QNA_CONTENT= #{QNA_CONTENT}
	WHERE QNA_NUM = #{QNA_NUM}
	AND MEM_ID = #{MEM_ID}

mapper.delete:

	DELETE FROM QNA
	WHERE QNA_NUM = #{QNA_NUM}
	AND MEM_ID = #{MEM_ID}

mapper.update:

	UPDATE REVIEW SET 
		RE_TITLE= #{RE_TITLE},
		RE_CONTENT= #{RE_CONTENT}
	WHERE RE_NUM = #{RE_NUM}


	UPDATE CART SET 
		CART_CNT= #{CART_CNT}
	WHERE CART_NUM = #{CART_NUM}
	AND MEM_ID = #{MEM_ID}

mapper.delete:

	DELETE CART 
	WHERE CART_NUM = #{CART_NUM}
	AND MEM_ID = #{MEM_ID}
